import React, { useState } from 'react';

const LessonPage = ({ lessonNumber, nextLesson, previousLesson }) => {
  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [score, setScore] = useState(null);

  const questions = [
    {
      question: 'What is phishing?',
      options: [
        'A technique to secure passwords',
        'An attempt to steal sensitive information by pretending to be a trustworthy entity',
        'A type of firewall',
        'A form of encryption',
      ],
      correctAnswer: 'An attempt to steal sensitive information by pretending to be a trustworthy entity',
    },
    {
      question: 'What should you do if you receive an unsolicited email asking for personal information?',
      options: [
        'Ignore the email',
        'Click the link and provide the information',
        'Mark the email as spam',
        'Respond with your personal information',
      ],
      correctAnswer: 'Mark the email as spam',
    },
  ];

  const handleAnswerSelect = (answer) => {
    setSelectedAnswer(answer);
  };

  const handleSubmit = () => {
    if (selectedAnswer === questions[0].correctAnswer) {
      setScore(1);
    } else {
      setScore(0);
    }
  };

  return (
    <div>
      <h1>Lesson {lessonNumber}: Cybersecurity for Marketing Professionals</h1>
      <p>{questions[0].question}</p>
      <div>
        {questions[0].options.map((option, index) => (
          <div key={index}>
            <input
              type="radio"
              id={`option-${index}`}
              name="answer"
              value={option}
              checked={selectedAnswer === option}
              onChange={() => handleAnswerSelect(option)}
            />
            <label htmlFor={`option-${index}`}>{option}</label>
          </div>
        ))}
      </div>

      <button onClick={handleSubmit}>Submit</button>
      {score !== null && <p>Your score: {score}</p>}

      <div>
        <button onClick={previousLesson}>Previous Lesson</button>
        <button onClick={nextLesson}>Next Lesson</button>
      </div>
    </div>
  );
};

export default LessonPage;
