import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const PasswordSecurityQuiz = () => {
  const [answer, setAnswer] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => setAnswer(e.target.value);

  const handleSubmit = () => {
    if (answer === 'c') {
      alert('Correct! Well done.');
    } else {
      alert('Incorrect. Try again!');
    }
  };

  return (
    <div className="quiz-container">
      <h2>Password Security Quiz</h2>
      <p>Question 1: What is the best practice for creating a secure password?</p>
      <div className="quiz-options">
        <label>
          <input type="radio" name="question" value="a" onChange={handleChange} />
          Use your birth date as your password.
        </label>
        <label>
          <input type="radio" name="question" value="b" onChange={handleChange} />
          Use the same password for all accounts.
        </label>
        <label>
          <input type="radio" name="question" value="c" onChange={handleChange} />
          Use a mix of uppercase, lowercase, numbers, and special characters for each password.
        </label>
      </div>
      <button onClick={handleSubmit}>Submit Answer</button>
      <button onClick={() => navigate('/lessons/password-security')}>Back to Lesson</button>
    </div>
  );
};

export default PasswordSecurityQuiz;
