import React, { useState } from 'react';
import './SocialEngineeringQuiz.css';
import { useNavigate } from 'react-router-dom';

const SocialEngineeringQuiz = () => {
  const navigate = useNavigate();
  const questions = [
    {
      id: 1,
      question: 'What is social engineering in the context of cybersecurity?',
      options: [
        { text: 'Using software to hack into systems', correct: false },
        { text: 'Manipulating people into revealing confidential information', correct: true },
        { text: 'Encrypting passwords for security', correct: false },
        { text: 'Installing firewalls on devices', correct: false }
      ]
    },
    {
      id: 2,
      question: 'Which of the following is a common social engineering tactic?',
      options: [
        { text: 'Sending fake job offers to extract information', correct: true },
        { text: 'Using a strong antivirus', correct: false },
        { text: 'Performing regular system updates', correct: false },
        { text: 'Encrypting data files', correct: false }
      ]
    },
    {
      id: 3,
      question: 'How can you protect yourself from social engineering?',
      options: [
        { text: 'Trust anyone who contacts you with a sense of urgency', correct: false },
        { text: 'Verify identity before sharing sensitive info', correct: true },
        { text: 'Use the same password everywhere', correct: false },
        { text: 'Ignore security training', correct: false }
      ]
    },
    {
      id: 4,
      question: 'What is "pretexting" in social engineering?',
      options: [
        { text: 'A phishing scam involving emails', correct: false },
        { text: 'Creating a fabricated scenario to steal information', correct: true },
        { text: 'Spamming users with fake links', correct: false },
        { text: 'Intercepting network traffic', correct: false }
      ]
    }
  ];

  const [selectedOption, setSelectedOption] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);

  const handleAnswerSelection = (index) => {
    setSelectedOption(index);
  };

  const handleNextQuestion = () => {
    if (selectedOption !== null) {
      const currentQuestion = questions[currentQuestionIndex];
      if (currentQuestion.options[selectedOption].correct) {
        setCorrectAnswers((prev) => prev + 1);
      }

      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex((prev) => prev + 1);
        setSelectedOption(null);
      } else {
        setQuizCompleted(true);
      }
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex((prev) => prev - 1);
      setSelectedOption(null);
    }
  };

  const handleRetry = () => {
    setCurrentQuestionIndex(0);
    setCorrectAnswers(0);
    setQuizCompleted(false);
    setSelectedOption(null);
  };

  const handleReturnToLesson = () => {
    navigate('/lessons/social-engineering');
  };

  const handleNextLesson = () => {
    navigate('/lessons/vishing');
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-card">
      <h2>Social Engineering Quiz</h2>
      {!quizCompleted ? (
        <>
          <div className="question">
            <p>{currentQuestion.question}</p>
          </div>
          <div className="options">
            {currentQuestion.options.map((option, index) => {
              const isSelected = selectedOption === index;
              const isCorrect = option.correct && isSelected;
              const isWrong = !option.correct && isSelected;

              return (
                <button
                  key={index}
                  onClick={() => handleAnswerSelection(index)}
                  className={
                    isSelected
                      ? isCorrect
                        ? 'selected correct'
                        : 'selected wrong'
                      : ''
                  }
                >
                  {option.text}
                </button>
              );
            })}
          </div>
          <div className="navigation-buttons">
            {currentQuestionIndex > 0 && (
              <button onClick={handlePreviousQuestion}>
                Previous Question
              </button>
            )}
            <button onClick={handleNextQuestion}>Next Question</button>
          </div>
        </>
      ) : (
        <div className="result">
          <h3>Quiz Finished!</h3>
          <p>You got {correctAnswers} out of {questions.length} correct.</p>
          {correctAnswers === questions.length ? (
            <div className="result-buttons">
              <button onClick={handleNextLesson} className="next-lesson-btn">
                Next Lesson
              </button>
              <button onClick={handleRetry} className="retry-btn">
                Retry Quiz
              </button>
            </div>
          ) : (
            <div className="result-buttons">
              <button onClick={handleRetry} className="retry-btn">
                Retry Quiz
              </button>
              <button onClick={handleReturnToLesson} className="return-btn">
                Return to Lesson
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SocialEngineeringQuiz;
