import React, { useState } from 'react';
import './Navbar.css';  // Make sure the styles are in the correct CSS file

const Navbar = () => {
  const [searchQuery, setSearchQuery] = useState("");

  // Function to handle search input change
  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  // Function to handle form submission (e.g., if you want to filter or search for lessons)
  const handleSearchSubmit = (event) => {
    event.preventDefault();
    console.log("Searching for:", searchQuery); // You can add logic to search through lessons
  };

  return (
    <nav className="navbar">
      <h2>Marketing Secure</h2>
      <form className="search-form" onSubmit={handleSearchSubmit}>
        <input
          type="text"
          placeholder="Search Lessons..."
          value={searchQuery}
          onChange={handleSearchChange}
          className="search-bar"
        />
        <button type="submit" className="search-button">Search</button>
      </form>
    </nav>
  );
};

export default Navbar;
