import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './FinalQuiz.css';

const FinalQuiz = () => {
  const questions = [
    {
      id: 1,
      question: 'What is phishing?',
      options: [
        { text: 'A form of social engineering', correct: true },
        { text: 'A type of password manager', correct: false },
        { text: 'A computer virus', correct: false },
        { text: 'None of the above', correct: false }
      ]
    },
    {
      id: 2,
      question: 'What is the best way to create a strong password?',
      options: [
        { text: 'Use personal information', correct: false },
        { text: 'Use a mix of letters, numbers, and symbols', correct: true },
        { text: 'Write it down and keep it safe', correct: false },
        { text: 'Use the same password for all accounts', correct: false }
      ]
    },
    {
      id: 3,
      question: 'Which of the following is a sign of social engineering?',
      options: [
        { text: 'Suspicious email from an unknown sender', correct: true },
        { text: 'A regular software update', correct: false },
        { text: 'Normal system performance', correct: false },
        { text: 'Automatic website redirection', correct: false }
      ]
    },
    {
      id: 4,
      question: 'What is vishing?',
      options: [
        { text: 'Voice-based phishing', correct: true },
        { text: 'A type of malware', correct: false },
        { text: 'An email scam', correct: false },
        { text: 'A fake login page', correct: false }
      ]
    },
    {
      id: 5,
      question: 'Which of the following is a good password manager?',
      options: [
        { text: 'Storing passwords in a text file', correct: false },
        { text: 'Using the same password for every account', correct: false },
        { text: 'Using a trusted password manager', correct: true },
        { text: 'Sharing passwords with others', correct: false }
      ]
    },
    {
      id: 6,
      question: 'What is a common phishing tactic?',
      options: [
        { text: 'Sending a text from a known contact', correct: false },
        { text: 'Offering an unsolicited prize', correct: true },
        { text: 'Sending a legitimate email from your bank', correct: false },
        { text: 'Asking for a password reset link', correct: false }
      ]
    },
    {
      id: 7,
      question: 'What should you do if you receive a suspicious phone call asking for sensitive information?',
      options: [
        { text: 'Provide the information immediately', correct: false },
        { text: 'Hang up and call the company back using a verified number', correct: true },
        { text: 'Ignore it and do nothing', correct: false },
        { text: 'Call the police right away', correct: false }
      ]
    },
    {
      id: 8,
      question: 'What is the first step in identifying a phishing website?',
      options: [
        { text: 'Check for a padlock icon in the browser address bar', correct: true },
        { text: 'Ensure the website loads quickly', correct: false },
        { text: 'Make sure the website is in English', correct: false },
        { text: 'Click on all the links to verify the site', correct: false }
      ]
    },
    {
      id: 9,
      question: 'What is the primary goal of social engineering?',
      options: [
        { text: 'To hack into a computer system', correct: false },
        { text: 'To manipulate people into revealing confidential information', correct: true },
        { text: 'To steal credit card information', correct: false },
        { text: 'To install malware', correct: false }
      ]
    },
    {
      id: 10,
      question: 'What should you do if you think you’ve fallen victim to vishing?',
      options: [
        { text: 'Do nothing and ignore the call', correct: false },
        { text: 'Immediately report it to the authorities', correct: false },
        { text: 'Call your bank and report the incident', correct: true },
        { text: 'Wait for the attacker to call you again', correct: false }
      ]
    }
  ];

  const [selectedOption, setSelectedOption] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);

  const handleAnswerSelection = (index) => {
    setSelectedOption(index);
  };

  const handleNextQuestion = () => {
    if (selectedOption !== null) {
      const currentQuestion = questions[currentQuestionIndex];
      if (currentQuestion.options[selectedOption].correct) {
        setCorrectAnswers((prev) => prev + 1);
      }

      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex((prev) => prev + 1);
        setSelectedOption(null); // Reset selection for next question
      } else {
        setQuizCompleted(true);
      }
    }
  };

  const handleRetry = () => {
    setCurrentQuestionIndex(0);
    setCorrectAnswers(0);
    setQuizCompleted(false);
    setSelectedOption(null);
  };

  const isFullMarks = correctAnswers === questions.length;

  return (
    <div className="quiz-card">
      <h2>Final Cybersecurity Quiz</h2>
      {!quizCompleted ? (
        <>
          <div className="question">
            <p>{questions[currentQuestionIndex].question}</p>
          </div>
          <div className="options">
            {questions[currentQuestionIndex].options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswerSelection(index)}
                className={selectedOption === index ? 'selected' : ''}
              >
                {option.text}
              </button>
            ))}
          </div>
          <button onClick={handleNextQuestion}>Next Question</button>
        </>
      ) : (
        <div className="result">
          {isFullMarks ? (
            <>
              <h3>Congratulations! You Passed the Course!</h3>
              <p>You got all {questions.length} questions correct!</p>
            </>
          ) : (
            <p>You got {correctAnswers} out of {questions.length} correct.</p>
          )}
          <button onClick={handleRetry}>Retry Quiz</button>
          {isFullMarks && (
            <Link to="/" className="return-home-button">Return to Homepage</Link>
          )}
        </div>
      )}
    </div>
  );
};

export default FinalQuiz;
