import React, { useState } from 'react';

const PhishingQuiz = () => {
  const questions = [
    {
      question: 'What should you check when receiving an unexpected email?',
      options: ['The subject line', 'The sender’s email address', 'The email’s color scheme'],
      correctAnswer: 'The sender’s email address',
    },
    {
      question: 'What is a phishing attack?',
      options: ['A type of software bug', 'An attack that tries to get sensitive information', 'A legal business practice'],
      correctAnswer: 'An attack that tries to get sensitive information',
    },
    {
      question: 'What is one way to verify a suspicious email?',
      options: ['Ignore the email', 'Click the link', 'Contact the person through another verified method'],
      correctAnswer: 'Contact the person through another verified method',
    },
    {
      question: 'What is spear-phishing?',
      options: ['Phishing targeting a specific individual or organization', 'A phishing attack that uses automated bots', 'A form of phishing done through social media'],
      correctAnswer: 'Phishing targeting a specific individual or organization',
    },
    {
      question: 'Which of these is a common phishing tactic?',
      options: ['Sending personalized messages that seem urgent', 'Providing a secure login link', 'Having a real-time customer support response'],
      correctAnswer: 'Sending personalized messages that seem urgent',
    },
  ];

  const [userAnswers, setUserAnswers] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswerChange = (answer) => {
    const updatedAnswers = [...userAnswers];
    updatedAnswers[currentQuestionIndex] = answer;
    setUserAnswers(updatedAnswers);
  };

  const handleNext = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    } else {
      handleSubmit();
    }
  };

  const handleSubmit = () => {
    let calculatedScore = 0;
    questions.forEach((question, index) => {
      if (userAnswers[index] === question.correctAnswer) {
        calculatedScore++;
      }
    });
    setScore(calculatedScore);
    setIsSubmitted(true);
  };

  return (
    <div className="quiz-container">
      <h2>Phishing Quiz</h2>
      {!isSubmitted ? (
        <div>
          <div className="question">
            <p>{questions[currentQuestionIndex].question}</p>
            {questions[currentQuestionIndex].options.map((option, i) => (
              <label key={i}>
                <input
                  type="radio"
                  name={`question-${currentQuestionIndex}`}
                  value={option}
                  onChange={() => handleAnswerChange(option)}
                  checked={userAnswers[currentQuestionIndex] === option}
                />
                {option}
              </label>
            ))}
          </div>
          <button onClick={handleNext}>
            {currentQuestionIndex < questions.length - 1 ? 'Next Question' : 'Submit Quiz'}
          </button>
        </div>
      ) : (
        <div>
          <p>Your Score: {score}/{questions.length}</p>
          {score === questions.length ? (
            <p>Great job! You answered everything correctly.</p>
          ) : (
            <p>Try again to improve your score.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default PhishingQuiz;
