import React from 'react';
import { useParams, Link } from 'react-router-dom';
import LessonCard from './LessonCard';

const lessons = {
  phishing: {
    title: "Phishing",
    content: `Phishing is a common cyber attack where hackers impersonate legitimate companies or people to trick employees into clicking malicious links or giving away sensitive data.`,
    scenario: `ðŸ§  Scenario:
You're running a paid campaign on Google Ads when you receive an email saying your account is under review. The sender looks like â€œads-support@goog1e.comâ€ and asks you to log in to â€œverifyâ€ your billing details. You click â€” but it's a fake site. Just one click can give hackers access to your credentials and budgets.`,
    nextLink: "/lesson/password-security",
  },
  "password-security": {
    title: "Password Security",
    content: `Weak passwords are a hackerâ€™s best friend. Avoid using names, birthdays, or marketing tool names like â€œhubspot123â€. Use a password manager and enable two-factor authentication (2FA) for all tools including CMS, email marketing platforms, and analytics dashboards.`,
    scenario: `ðŸ§  Scenario:
You use â€œMailchimp2024â€ for your email platform and reuse it for multiple accounts. A data breach leaks this password, and within minutes, someone sends spam emails from your marketing domain. Now your domain is blacklisted, and your sender reputation drops overnight.`,
    nextLink: "/lesson/social-engineering",
  },
  "social-engineering": {
    title: "Social Engineering",
    content: `Social engineering exploits human psychology rather than technical vulnerabilities. Hackers might call pretending to be IT or a client asking for access to your campaign data. Always verify requests, especially those that involve urgency or secrecy.`,
    scenario: `ðŸ§  Scenario:
A â€œclientâ€ messages you on LinkedIn asking to quickly review a new campaign brief in Google Docs. The doc requires a login, but after entering your credentials, nothing happens. Meanwhile, your Google account gets compromised â€” including access to analytics, drive, and shared team assets.`,
    nextLink: "/lesson/vishing",
  },
  vishing: {
    title: "Vishing",
    content: `Vishing is voice phishing â€” scammers call you pretending to be from support teams, urging you to â€œconfirmâ€ sensitive credentials. Marketing pros often get fake calls claiming ad account suspension or SEO issues. Always hang up and call the real support line directly.`,
    scenario: `ðŸ§  Scenario:
You get a call claiming to be from â€œMeta Ads Support,â€ saying your ad account is flagged for suspicious activity. They urgently ask for your business manager ID and email. The moment you give it, your ad accounts are hijacked, running fraudulent ads under your brandâ€™s name.`,
    nextLink: null, // final lesson
  }
};

const LessonPage = () => {
  const { topicId } = useParams();
  const lesson = lessons[topicId];

  if (!lesson) {
    return <div className="app-wrapper"><h2>Lesson not found</h2></div>;
  }

  return (
    <div className="app-wrapper">
      <LessonCard
        title={lesson.title}
        content={lesson.content}
        scenario={lesson.scenario}
        nextLink={lesson.nextLink}
      />
    </div>
  );
};

export default LessonPage;
