import React from 'react';
import { Link } from 'react-router-dom';

const topics = [
  { id: 1, name: 'Phishing Attacks', description: 'Learn how to identify and avoid phishing scams.' },
  { id: 2, name: 'Password Security', description: 'Understand the importance of strong password practices.' },
];

const TopicPage = () => {
  return (
    <div>
      <h2>Cybersecurity Topics</h2>
      {topics.map((topic) => (
        <div key={topic.id}>
          <h3>{topic.name}</h3>
          <p>{topic.description}</p>
          <Link to={`/quiz/${topic.id}`}>Take Quiz</Link>
        </div>
      ))}
    </div>
  );
};

export default TopicPage;
