import React from 'react';
import './LessonCard.css';

const LessonCard = ({ title, content, scenario, nextLink }) => {
  return (
    <div className="lesson-card">
      <h2 className="lesson-title">{title}</h2>
      <p className="lesson-content">{content}</p>
      
      {scenario && (
        <>
          <h3 className="scenario-title">ðŸ§  Scenario:</h3>
          <p className="scenario-content">{scenario}</p>
        </>
      )}

      {nextLink && (
        <div className="next-button-container">
          <a href={nextLink}>
            <button className="next-button">Next Lesson</button>
          </a>
        </div>
      )}
    </div>
  );
};

export default LessonCard;
