import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    return localStorage.getItem('darkMode') === 'true';
  });

  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
    localStorage.setItem('darkMode', isDarkMode);
  }, [isDarkMode]);

  const toggleDarkMode = () => {
    setIsDarkMode(prev => !prev);
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-title">Marketing Secure</Link>
      </div>
      <div className="navbar-right">
        <Link to="/lessons/phishing" className="navbar-link">Lessons</Link>
        <Link to="/quizzes/phishing" className="navbar-link">Quizzes</Link>
        <Link to="/topics" className="navbar-link">Topics</Link>
        <button className="dark-mode-toggle" onClick={toggleDarkMode}>
          {isDarkMode ? 'â˜€ Light Mode' : 'ðŸŒ™ Dark Mode'}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
