import React, { useState } from 'react';
import LessonCard from '../LessonCard';

const PhishingQuiz = () => {
  const [answers, setAnswers] = useState({
    question1: '',
    question2: '',
    question3: ''
  });
  const [score, setScore] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setAnswers({
      ...answers,
      [name]: value
    });
  };

  const handleSubmit = () => {
    let correctAnswers = 0;

    if (answers.question1 === 'option1') correctAnswers++;
    if (answers.question2 === 'option2') correctAnswers++;
    if (answers.question3 === 'option3') correctAnswers++;

    setScore(correctAnswers);
  };

  return (
    <LessonCard title="Phishing Quiz">
      <form>
        <ol>
          <li>
            <p>What is a common sign of a phishing email?</p>
            <label>
              <input type="radio" name="question1" value="option1" onChange={handleChange} /> Poor spelling and grammar
            </label>
            <label>
              <input type="radio" name="question1" value="option2" onChange={handleChange} /> Personalized greeting
            </label>
            <label>
              <input type="radio" name="question1" value="option3" onChange={handleChange} /> Secure website
            </label>
          </li>
          <li>
            <p>What should you do if you receive a suspicious link?</p>
            <label>
              <input type="radio" name="question2" value="option1" onChange={handleChange} /> Click it to investigate
            </label>
            <label>
              <input type="radio" name="question2" value="option2" onChange={handleChange} /> Do not click and report it
            </label>
            <label>
              <input type="radio" name="question2" value="option3" onChange={handleChange} /> Open it in a new tab
            </label>
          </li>
          <li>
            <p>Why do attackers use urgency in phishing emails?</p>
            <label>
              <input type="radio" name="question3" value="option1" onChange={handleChange} /> To cause panic
            </label>
            <label>
              <input type="radio" name="question3" value="option2" onChange={handleChange} /> To increase curiosity
            </label>
            <label>
              <input type="radio" name="question3" value="option3" onChange={handleChange} /> To get a quick response
            </label>
          </li>
        </ol>
        <button type="button" onClick={handleSubmit}>Submit</button>
      </form>

      {score !== null && (
        <div className="quiz-result">
          <h3>Your score: {score}/3</h3>
        </div>
      )}
    </LessonCard>
  );
};

export default PhishingQuiz;
