import React from 'react';
import { useParams, Link } from 'react-router-dom';

const lessons = {
  "phishing": {
    title: "Phishing",
    content: `Phishing is a common cyber attack where hackers impersonate legitimate companies or people to trick employees into clicking malicious links or giving away sensitive data. 
    As a marketer, you may receive emails from services like Google Ads, Meta, or CRM tools — always check sender addresses, hover over links, and never download unexpected attachments.`,
    next: "password-security"
  },
  "password-security": {
    title: "Password Security",
    content: `Weak passwords are a hacker’s best friend. Avoid using names, birthdays, or marketing tool names like “hubspot123”. Use a password manager and enable two-factor authentication (2FA) for all tools including CMS, email marketing platforms, and analytics dashboards.`,
    next: "social-engineering"
  },
  "social-engineering": {
    title: "Social Engineering",
    content: `Social engineering exploits human psychology rather than technical vulnerabilities. Hackers might call pretending to be IT or a client asking for access to your campaign data. Always verify requests, especially those that involve urgency or secrecy.`,
    next: "vishing"
  },
  "vishing": {
    title: "Vishing",
    content: `Vishing is voice phishing — scammers call you pretending to be from support teams, urging you to “confirm” sensitive credentials. Marketing pros often get fake calls claiming ad account suspension or SEO issues. Always hang up and call the real support line directly.`,
    next: null // final topic
  }
};

const LessonPage = () => {
  const { topicId } = useParams();
  const lesson = lessons[topicId];

  if (!lesson) {
    return <div className="app-wrapper"><h2>Lesson not found</h2></div>;
  }

  return (
    <div className="app-wrapper">
      <h1>{lesson.title}</h1>
      <p style={{ whiteSpace: 'pre-line' }}>{lesson.content}</p>

      {lesson.next ? (
        <Link to={`/lesson/${lesson.next}`}>
          <button style={{ marginTop: '20px' }}>Next Lesson</button>
        </Link>
      ) : (
        <Link to="/topics">
          <button style={{ marginTop: '20px' }}>Back to Topics</button>
        </Link>
      )}
    </div>
  );
};

export default LessonPage;
