import React from 'react';
import { useParams, Link } from 'react-router-dom';
import LessonCard from './LessonCard';

const lessons = {
  phishing: {
    title: "Phishing",
    content: `Phishing is a common cyber attack where hackers impersonate legitimate companies or people to trick employees into clicking malicious links or giving away sensitive data.`,
    scenario: `üß† Scenario:
You're running a paid campaign on Google Ads when you receive an email saying your account is under review. The sender looks like ‚Äúads-support@goog1e.com‚Äù and asks you to log in to ‚Äúverify‚Äù your billing details. You click ‚Äî but it's a fake site. Just one click can give hackers access to your credentials and budgets.`,
    nextLink: "/lesson/password-security",
    prevLink: null,
  },
  "password-security": {
    title: "Password Security",
    content: `Weak passwords are a hacker‚Äôs best friend. Avoid using names, birthdays, or marketing tool names like ‚Äúhubspot123‚Äù. Use a password manager and enable two-factor authentication (2FA) for all tools including CMS, email marketing platforms, and analytics dashboards.`,
    scenario: `üß† Scenario:
You use ‚ÄúMailchimp2024‚Äù for your email platform and reuse it for multiple accounts. A data breach leaks this password, and within minutes, someone sends spam emails from your marketing domain. Now your domain is blacklisted, and your sender reputation drops overnight.`,
    nextLink: "/lesson/social-engineering",
    prevLink: "/lesson/phishing",
  },
  "social-engineering": {
    title: "Social Engineering",
    content: `Social engineering exploits human psychology rather than technical vulnerabilities. Hackers might call pretending to be IT or a client asking for access to your campaign data. Always verify requests, especially those that involve urgency or secrecy.`,
    scenario: `üß† Scenario:
A ‚Äúclient‚Äù messages you on LinkedIn asking to quickly review a new campaign brief in Google Docs. The doc requires a login, but after entering your credentials, nothing happens. Meanwhile, your Google account gets compromised ‚Äî including access to analytics, drive, and shared team assets.`,
    nextLink: "/lesson/vishing",
    prevLink: "/lesson/password-security",
  },
  vishing: {
    title: "Vishing",
    content: `Vishing is voice phishing ‚Äî scammers call you pretending to be from support teams, urging you to ‚Äúconfirm‚Äù sensitive credentials. Marketing pros often get fake calls claiming ad account suspension or SEO issues. Always hang up and call the real support line directly.`,
    scenario: `üß† Scenario:
You get a call claiming to be from ‚ÄúMeta Ads Support,‚Äù saying your ad account is flagged for suspicious activity. They urgently ask for your business manager ID and email. The moment you give it, your ad accounts are hijacked, running fraudulent ads under your brand‚Äôs name.`,
    nextLink: null, // final lesson
    prevLink: "/lesson/social-engineering",
  }
};

const LessonPage = () => {
  const { topicId } = useParams();
  const lesson = lessons[topicId];

  if (!lesson) {
    return <div className="app-wrapper"><h2>Lesson not found</h2></div>;
  }

  return (
    <div className="app-wrapper">
      <LessonCard
        title={lesson.title}
        content={lesson.content}
        scenario={lesson.scenario}
      />

      {/* Show Previous and Next buttons for all but the final lesson */}
      {lesson.prevLink && (
        <div className="navigation-buttons">
          <Link to={lesson.prevLink}>
            <button className="prev-button">Previous Lesson</button>
          </Link>
        </div>
      )}

      {lesson.nextLink && (
        <div className="navigation-buttons">
          <Link to={lesson.nextLink}>
            <button className="next-button">Next Lesson</button>
          </Link>
        </div>
      )}

      {/* Show options after the final lesson */}
      {lesson.nextLink === null && (
        <div className="next-button-container">
          <p>What would you like to do next?</p>
          <div className="options-container">
            <Link to="/topics">
              <button className="next-button">Return to Topics</button>
            </Link>
            <Link to="/quiz">
              <button className="next-button">Start Quiz</button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default LessonPage;
