import React, { useState } from 'react';
import './VishingQuiz.css';
import { useNavigate } from 'react-router-dom';

const VishingQuiz = () => {
  const navigate = useNavigate();
  const questions = [
    {
      id: 1,
      question: 'What is vishing?',
      options: [
        { text: 'Phishing through emails', correct: false },
        { text: 'Voice-based phishing over phone calls', correct: true },
        { text: 'Hacking wireless networks', correct: false },
        { text: 'Using fake websites to gather data', correct: false }
      ]
    },
    {
      id: 2,
      question: 'Which of these is a common vishing technique?',
      options: [
        { text: 'Sending fake invoices via text', correct: false },
        { text: 'Calling victims pretending to be from their bank', correct: true },
        { text: 'Asking for credentials via pop-ups', correct: false },
        { text: 'Redirecting web traffic to scam sites', correct: false }
      ]
    },
    {
      id: 3,
      question: 'What should you do if you receive a suspicious call asking for sensitive info?',
      options: [
        { text: 'Provide the info quickly to end the call', correct: false },
        { text: 'Hang up and contact the organization directly', correct: true },
        { text: 'Put the caller on hold indefinitely', correct: false },
        { text: 'Engage them to waste their time', correct: false }
      ]
    },
    {
      id: 4,
      question: 'Why is vishing effective?',
      options: [
        { text: 'People trust emails more than calls', correct: false },
        { text: 'It uses fear or urgency to manipulate people over the phone', correct: true },
        { text: 'It is hard to detect antivirus programs', correct: false },
        { text: 'It requires advanced hacking skills', correct: false }
      ]
    }
  ];

  const [selectedOption, setSelectedOption] = useState(null);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [correctAnswers, setCorrectAnswers] = useState(0);
  const [quizCompleted, setQuizCompleted] = useState(false);

  const handleAnswerSelection = (index) => {
    setSelectedOption(index);
  };

  const handleNextQuestion = () => {
    if (selectedOption !== null) {
      const currentQuestion = questions[currentQuestionIndex];
      if (currentQuestion.options[selectedOption].correct) {
        setCorrectAnswers((prev) => prev + 1);
      }

      if (currentQuestionIndex < questions.length - 1) {
        setCurrentQuestionIndex((prev) => prev + 1);
        setSelectedOption(null);
      } else {
        setQuizCompleted(true);
      }
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex((prev) => prev - 1);
      setSelectedOption(null);
    }
  };

  const handleRetry = () => {
    setCurrentQuestionIndex(0);
    setCorrectAnswers(0);
    setQuizCompleted(false);
    setSelectedOption(null);
  };

  const handleReturnToLesson = () => {
    navigate('/lessons/vishing');
  };

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="quiz-card">
      <h2>Vishing Quiz</h2>
      {!quizCompleted ? (
        <>
          <div className="question">
            <p>{currentQuestion.question}</p>
          </div>
          <div className="options">
            {currentQuestion.options.map((option, index) => {
              const isSelected = selectedOption === index;
              const isCorrect = option.correct && isSelected;
              const isWrong = !option.correct && isSelected;

              return (
                <button
                  key={index}
                  onClick={() => handleAnswerSelection(index)}
                  className={
                    isSelected
                      ? isCorrect
                        ? 'selected correct'
                        : 'selected wrong'
                      : ''
                  }
                >
                  {option.text}
                </button>
              );
            })}
          </div>
          <div className="navigation-buttons">
            {currentQuestionIndex > 0 && (
              <button onClick={handlePreviousQuestion}>
                Previous Question
              </button>
            )}
            <button onClick={handleNextQuestion}>Next Question</button>
          </div>
        </>
      ) : (
        <div className="result">
          <h3>Quiz Finished!</h3>
          <p>You got {correctAnswers} out of {questions.length} correct.</p>
          {correctAnswers === questions.length ? (
            <div className="result-buttons">
              <button onClick={handleRetry} className="retry-btn">
                Retry Quiz
              </button>
            </div>
          ) : (
            <div className="result-buttons">
              <button onClick={handleRetry} className="retry-btn">
                Retry Quiz
              </button>
              <button onClick={handleReturnToLesson} className="return-btn">
                Return to Lesson
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default VishingQuiz;
