import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const savedMode = localStorage.getItem('darkMode');
    if (savedMode === 'true') {
      setIsDarkMode(true);
      document.body.classList.add('dark-mode');
    }
  }, []);

  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.body.classList.toggle('dark-mode');
    localStorage.setItem('darkMode', !isDarkMode);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-left">
          <Link to="/" className="navbar-title">Marketing Secure</Link>
        </div>

        <div className="navbar-right">
          <Link to="/lessons/phishing" className="navbar-link">Lessons</Link>
          <Link to="/quizzes/phishing" className="navbar-link">Quizzes</Link>
          <Link to="/topics" className="navbar-link">Topics</Link>
          <Link to="/quizzes" className="navbar-link">Quizzes Hub</Link>
          <button onClick={toggleDarkMode} className="dark-mode-toggle">
            {isDarkMode ? 'Light Mode' : 'Dark Mode'}
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
