import React, { useState } from 'react';
import './SocialEngineeringQuiz.css';

const SocialEngineeringQuiz = () => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [showResult, setShowResult] = useState(false);

  const questions = [
    {
      question: 'What is social engineering?',
      options: ['Exploiting people to gain access to confidential information', 'A technique to hack into secure systems', 'Manipulating network traffic', 'Exploiting security vulnerabilities'],
      correctAnswer: 'Exploiting people to gain access to confidential information'
    },
    {
      question: 'What should you do if you receive an unsolicited phone call asking for sensitive information?',
      options: ['Provide the information if they seem legitimate', 'Hang up and contact the organization directly to verify', 'Ask them for their personal details', 'None of the above'],
      correctAnswer: 'Hang up and contact the organization directly to verify'
    }
  ];

  const handleAnswerSelection = (answer) => {
    const updatedAnswers = [...userAnswers];
    updatedAnswers[currentQuestionIndex] = answer;
    setUserAnswers(updatedAnswers);
  };

  const handleNextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePreviousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const handleSubmit = () => {
    setShowResult(true);
  };

  const retryQuiz = () => {
    setUserAnswers([]);
    setCurrentQuestionIndex(0);
    setShowResult(false);
  };

  const goToNextLesson = () => {
    // Replace with actual lesson navigation logic
    alert('Redirecting to the next lesson');
  };

  return (
    <div className="quiz-container">
      <div className="quiz-card">
        {!showResult ? (
          <>
            <div className="question">
              <h3>{questions[currentQuestionIndex].question}</h3>
              <div className="options">
                {questions[currentQuestionIndex].options.map((option, index) => (
                  <button
                    key={index}
                    onClick={() => handleAnswerSelection(option)}
                    className={`option ${userAnswers[currentQuestionIndex] === option ? 'selected' : ''}`}
                  >
                    {option}
                  </button>
                ))}
              </div>
            </div>
            <div className="navigation-buttons">
              {currentQuestionIndex > 0 && <button onClick={handlePreviousQuestion}>Previous</button>}
              {currentQuestionIndex < questions.length - 1 && <button onClick={handleNextQuestion}>Next</button>}
              {currentQuestionIndex === questions.length - 1 && (
                <button onClick={handleSubmit}>Submit</button>
              )}
            </div>
          </>
        ) : (
          <div className="result">
            <h3>Quiz Results</h3>
            <p>{userAnswers.filter((answer, index) => answer === questions[index].correctAnswer).length} out of {questions.length} correct</p>
            {userAnswers.filter((answer, index) => answer === questions[index].correctAnswer).length === questions.length ? (
              <button onClick={goToNextLesson}>Next Lesson</button>
            ) : (
              <>
                <button onClick={retryQuiz}>Retry</button>
                <button onClick={goToNextLesson}>Return to Lesson</button>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default SocialEngineeringQuiz;
