import React from 'react';
import { useNavigate } from 'react-router-dom';
import LessonCard from '../LessonCard';

const PasswordSecurityLesson = () => {
  const navigate = useNavigate();

  return (
    <LessonCard title="Password Security">
      <p>Using strong, unique passwords for each account is crucial. Consider using a password manager to store them securely.</p>
      <p><strong>Scenario:</strong> A marketer uses the same password across multiple platforms. One gets breached, compromising all others.</p>
      <div className="button-container">
        <button onClick={() => navigate('/quizzes/password-security')}>Take Quiz</button>
        <button onClick={() => navigate('/lessons/phishing')}>Previous Lesson</button>
      </div>
    </LessonCard>
  );
};

export default PasswordSecurityLesson;
