import React from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Ensure you are importing the updated Navbar CSS

const Navbar = () => {
  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-title">Marketing Secure</Link>
      </div>
      
      <div className="navbar-right">
        <Link to="/lessons/phishing" className="navbar-link">Lessons</Link>
        <Link to="/quizzes/phishing" className="navbar-link">Quizzes</Link>
        <Link to="/topics" className="navbar-link">Topics</Link>
        <button onClick={toggleDarkMode} style={{ color: 'white' }}>
          {isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
