import React from 'react';
import { useNavigate } from 'react-router-dom';
import LessonCard from '../LessonCard';

const PhishingLesson = () => {
  const navigate = useNavigate();

  return (
    <LessonCard title="Phishing Attacks">
      <p>Phishing is a technique where attackers trick users into revealing sensitive information like passwords or credit card numbers.</p>
      <p><strong>Scenario:</strong> You receive an email pretending to be from your CEO asking you to urgently purchase gift cards.</p>
      <div className="button-container">
        <button onClick={() => navigate('/quizzes/phishing')}>Take Quiz</button>
        <button onClick={() => navigate('/lessons/password-security')}>Next Lesson</button>
      </div>
    </LessonCard>
  );
};

export default PhishingLesson;
