import React, { useState } from 'react';
import './PhishingQuiz.css';

const PhishingQuiz = () => {
  const questions = [
    {
      question: "What is phishing?",
      options: ["A scam", "A programming language", "A website", "A game"],
      correctAnswer: "A scam",
    },
    {
      question: "What is a common sign of phishing?",
      options: ["Suspicious email address", "Inconsistent website design", "Unexpected pop-ups", "All of the above"],
      correctAnswer: "All of the above",
    },
    // Add more questions as needed
  ];

  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [quizCompleted, setQuizCompleted] = useState(false);
  const [score, setScore] = useState(0);

  const handleAnswer = (answer) => {
    const updatedAnswers = [...userAnswers];
    updatedAnswers[currentQuestionIndex] = answer;
    setUserAnswers(updatedAnswers);

    if (answer === questions[currentQuestionIndex].correctAnswer) {
      setScore(score + 1);
    }
  };

  const handleNext = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(currentQuestionIndex + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(currentQuestionIndex - 1);
    }
  };

  const handleSubmit = () => {
    setQuizCompleted(true);
  };

  const handleRetry = () => {
    setCurrentQuestionIndex(0);
    setUserAnswers([]);
    setQuizCompleted(false);
    setScore(0);
  };

  const handleReturnToLesson = () => {
    // Logic to return to the relevant lesson page
    // For example, using navigate() from react-router-dom
    // navigate('/lessons/phishing');
  };

  return (
    <div className="quiz-container">
      {!quizCompleted ? (
        <div className="quiz-card">
          <h2>{questions[currentQuestionIndex].question}</h2>
          <div className="options">
            {questions[currentQuestionIndex].options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleAnswer(option)}
                className={`option-button ${userAnswers[currentQuestionIndex] === option ? 'selected' : ''}`}
              >
                {option}
              </button>
            ))}
          </div>

          <div className="navigation-buttons">
            <button
              onClick={handlePrevious}
              disabled={currentQuestionIndex === 0}
              className="nav-button"
            >
              Previous
            </button>
            {currentQuestionIndex < questions.length - 1 ? (
              <button onClick={handleNext} className="nav-button">
                Next
              </button>
            ) : (
              <button onClick={handleSubmit} className="nav-button">
                Submit
              </button>
            )}
          </div>
        </div>
      ) : (
        <div className="quiz-result">
          <h2>Quiz Completed!</h2>
          <p>Your score: {score} / {questions.length}</p>
          {score === questions.length ? (
            <div className="result-buttons">
              <button onClick={() => handleReturnToLesson()} className="next-lesson-btn">
                Go to Next Lesson
              </button>
              <button onClick={handleRetry} className="retry-btn">
                Retry Quiz
              </button>
            </div>
          ) : (
            <div className="result-buttons">
              <button onClick={handleRetry} className="retry-btn">
                Retry Quiz
              </button>
              <button onClick={() => handleReturnToLesson()} className="return-btn">
                Return to Lesson
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default PhishingQuiz;
