import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css'; // Ensure you're importing the updated Navbar CSS

const Navbar = () => {
  const [isDarkMode, setIsDarkMode] = useState(true); // Default dark mode

  // Toggle the dark mode
  const toggleDarkMode = () => {
    setIsDarkMode((prevMode) => !prevMode); // Toggle the state
    document.body.classList.toggle('dark', !isDarkMode); // Add/remove 'dark' class to body
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="navbar-title">Marketing Secure</Link>
      </div>

      <div className="navbar-right">
        <Link to="/lessons/phishing" className="navbar-link">Lessons</Link>
        <Link to="/quizzes/phishing" className="navbar-link">Quizzes</Link>
        <Link to="/topics" className="navbar-link">Topics</Link>
        <button onClick={toggleDarkMode} className="dark-mode-toggle">
          {isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
